<blockquote data-mlsid="<!--{{ property:mlsid }}-->" cite="{{ property:url }}" id="property--<!--{{ property:id }}-->" data-property-id="<!--{{ property:id }}-->" class="vf-property-block" itemscope itemtype="https://schema.org/Place">

	<figure class="vf-property-block__image placeholder"><a class="vf-property-block__image--link" href="{{ property:url }}"><!--{{ property:thumbnail }}--></a></figure>

	<a class="vf-property-block__address" href="{{ property:url }}" itemprop="address"><!--{{ property:address }}--></a>

	<span class="vf-property-block__meta"><!--{{ property:meta }}--></span>

	<!--{{ compliance }}-->

	<span class="vf-property-block__flags--price vf-property-block__price"><!--{{ property:price }}--></span>

	<!--{{ flags }}-->

</blockquote>
